// react
import React, { Component } from 'react';

// third-party
import { Helmet } from 'react-helmet';

// application
import QuickSearch from './QuickSearch';
import SearchMenu from './SearchMenu';
import { Breadcrumb } from '../../shared/Breadcrumb';

// pages
import { IndexSearch } from './IndexSearch';
import { SearchCriterias } from './search-criterias/SearchCriterias';
import { SearchCustom } from './search-custom/SearchCustom';
import { SearchGeneral } from './search-general/SearchGeneral';
import { SearchGenericArticle } from './search-generic-article/SearchGenericArticle';
import { SearchManufacturer } from './search-manufacturer/SearchManufacturer';
import { SearchTyre } from './search-tyre/SearchTyre';
import { SearchUniversal } from './search-universal/SearchUniversal';
import { SearchVehicles } from './search-vehicles/SearchVehicles';


export class Search extends Component {

    constructor() {
        super();
        this.state = {
            currentSearch: 0
        };
    }

    renderCurrentSearch = () => {
        return <IndexSearch />;


        //switch (this.state.currentSearch) {
        //    case 0:
        //        return <IndexSearch />;
        //    case 1:
        //        return <SearchGeneral/>;
        //    case 2:
        //        return <SearchVehicles />;
        //    case 3:
        //        return <SearchGenericArticle />;
        //    case 4:
        //        return <SearchCriterias />;
        //    case 5:
        //        return <SearchManufacturer />;
        //    case 6:
        //        return <SearchCustom />;
        //    case 7:
        //        return <SearchUniversal />;
        //    case 8:
        //        return <SearchTyre />;
        //    default:
        //}
    }

    //changeSearchCallback = (id) => {
    //    this.setState({
    //        currentSearch: id
    //    });
    //}

    render() {
        return (
            <React.Fragment>
                <Helmet>
                    <title>Search page</title>
                    <meta name="description" content="search description" />
                </Helmet>

                <div className="ekat__search">
                    <div className="ekat__quick-search">
                        <QuickSearch />
                    </div>
                    <div className="ekat__search-container">
                        <div className="ekat__search-menu">
                            <SearchMenu />
                        </div>
                        <div className="ekat__search-body">
                            <div className="search-body">
                                <section className="breadcrumb-section">
                                    <Breadcrumb />
                                </section>
                                
                                <section className="search-section">
                                    <div className="search-main">
                                        {/* Left column */}
                                        <div className="search__banner-container">
                                            Left banner test
                                        </div>
                                        
                                        {this.renderCurrentSearch()}
                                        
                                        {/* Right column */}
                                        <div className="search__banner-container">
                                            Right banner test
                                        </div>
                                    </div>
                                </section>
                            </div>
                        </div>
                    </div>
                </div>
            </React.Fragment>
        )
    };
}
