// react
import React, { Component } from 'react';

// third-party
import { NavLink } from 'react-router-dom';
import { IconAlertSvg, IconInformationSvg, IconLegendSvg } from '../../content/images/svg';

// pages
import { Modal } from './../shared/Modal';

export class NavIcons extends Component {
    constructor() {
        super();

        this.showModal = this.showModal.bind(this);
        this.hideModal = this.hideModal.bind(this);

        this.state = {
            modal: false
        }
    }

    showModal = () => {
        this.setState({
            modal: true
        });
    };

    hideModal = () => {
        this.setState({
            modal: false
        });
    };

    renderModals() {
        if (this.state.modal) {
            return (
                <Modal name="modal" show={this.state.modal} onClose={this.hideModal}>
                    <div>
                        <p>Modal</p>
                        <p>Data</p>
                    </div>
                </Modal>
            );
        }
    }

    render() {
        return (
            <React.Fragment>
                <div className="nav-icons">
                    <div>
                        <a onClick={this.showModal}>
                            <span>
                                <IconInformationSvg />
                            </span>
                        </a>
                        {/*<button type="button" onClick={this.showModal}>
                            <span>
                                <IconInformationSvg />
                            </span>
                        </button>*/}
                        <NavLink exact activeClassName="selected" to="/">
                            <span>
                                <IconInformationSvg />
                            </span>
                        </NavLink>
                    </div>
                    <div>
                        <NavLink exact activeClassName="selected" to="/">
                            <span>
                                <IconAlertSvg />
                            </span>
                        </NavLink>
                    </div>
                    <div>
                        <NavLink exact activeClassName="selected" to="/">
                            <span>
                                <IconLegendSvg className="nav-icons__button-icon" />
                            </span>
                        </NavLink>
                    </div>
                </div>

                {this.renderModals()}
            </React.Fragment>
        );
    }
}
