// react
import React, { Component } from 'react';

// third-party
import PropTypes from 'prop-types';
import { Helmet } from 'react-helmet';
import { Redirect, Route, Switch } from 'react-router-dom';

// application
import Footer from './footer';
import Header from './header';
import MobileHeader from './mobile/MobileHeader';
import MobileMenu from './mobile/MobileMenu';

// pages

import { Search } from './pages/search/index';
import { Orders } from './pages/orders/index';
import { Options } from './pages/options/index';

// data stubs
import theme from '../data/theme';

function Layout(props) {
    const { headerLayout } = props;

        return (
            <React.Fragment>
                <Helmet>
                    <title>{theme.name}</title>
                    <meta name="description" content={theme.fullName} />
                </Helmet>

                <div className="ekat__framework">
                    {/*TODO: create responsive header MobileHeader*/}

                    <header className="ekat__header">
                        <Header />
                    </header>

                    <div className="ekat__body">
                        {/*
                         // Search
                         */}
                        <Route exact path="/pages/search" component={Search} />

                        {/*
                         // Orders
                         */}
                        <Route exact path="/pages/orders" component={Orders} />

                        {/*
                         // Options
                         */}
                        <Route exact path="/pages/options" component={Options} />

                        {/*
                         // Page Not Found - pages for errors
                         */}
                    </div>

                    <footer className="ekat__footer">
                        <Footer/>
                    </footer>
                </div>
            </React.Fragment>
        );
    
}

Layout.propTypes = {
    /**
     * header layout (default: 'classic')
     * one of ['classic', 'compact']
     */
    headerLayout: PropTypes.oneOf(['default', 'compact'])
};

Layout.defaultProps = {
    headerLayout: 'default',
};

export default Layout;
