// react
import React, { Component } from 'react';

// third-party
import ReactDOM from 'react-dom';

export class Sidebar extends Component {
    constructor(props) {
        super();

        this.state = {
            show: props.show,
            onClose: props.onClose,
            children: props.children
        }
    }

    renderSidebar = () => {
        const showHideClassName = this.state.show ? "sidebar sidebar-show" : "sidebar sidebar-fade";

        return (
            <div className={showHideClassName}>
                <div className="sidebar-header">
                    <h5 className="sidebar-title">
                        {this.props.modalTitle}
                    </h5>
                    <button type="button" onClick={this.state.onClose} className="sidebar-close btn">
                        <span aria-hidden="true"></span>
                    </button>
                </div>
                <div className="sidebar-body">
                    {this.state.children}
                </div>
                <div className="sidebar-footer">
                    footer
                </div>
            </div>
        );
    }

    render() {
        return (
            ReactDOM.createPortal(this.renderSidebar(), document.getElementById("modal"))
        );
    }
}
