// react
import React, { Component } from 'react';

// third-party
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useParams
} from "react-router-dom";

import { IconClose20Svg } from '../../../content/images/svg';


export default class SearchMenu extends Component {

    constructor(props) {
        super();

        this.state = {
            changeSearchCallback: props.changeSearchCallback,
            selectedItem: props.menuItem
        };
    }

    renderMenuLinks = () => {
        const menuLinks = [
            { id: 1, icon: <IconClose20Svg />, title: "Criteria search", url: '/search-criterias' },
            { id: 2, icon: <IconClose20Svg />, title: "Custom search", url: '/search-custom' },
            { id: 3, icon: <IconClose20Svg />, title: "General search", url: '/search-general' },
            { id: 4, icon: <IconClose20Svg />, title: "Search by part name", url: '/search-generic-article' },
            { id: 5, icon: <IconClose20Svg />, title: "Search by manufacturer", url: '/search-manufacturer' },
            { id: 6, icon: <IconClose20Svg />, title: "Tyre search", url: '/search-tyre' },
            { id: 7, icon: <IconClose20Svg />, title: "Universal search", url: '/search-universal' },
            { id: 8, icon: <IconClose20Svg />, title: "Vehicle search", url: '/search-vehicles' }
        ];
        
        return (
            menuLinks.map((item, index, menuItem) => (
                <Router>
                    <li key={item.url} className={this.state.selectedItem === menuItem ? "search-menu__item selected" : "search-menu__item"}>
                        <Link to={item.url}>
                            <span>
                                {item.icon}
                                <span>
                                    {item.title}
                                </span>
                            </span>
                        </Link>
                    </li>
                    <Switch>
                        <Route path="/:${this.props.match.params.id}" />
                    </Switch>
                </Router>
            ))
        );
    }

    render() {
        return (
            <Router>
                <div className="search-menu">
                    <ul className="search-menu__list">
                        {this.renderMenuLinks()}
                    </ul>
                </div>
               
            </Router>
        );
    }
}
