// react
import React, { Component } from 'react';

// third-party
import ReactDOM from 'react-dom';
import { NavLink } from 'react-router-dom';

// data
import data from './../../data/data';

export class Sidebar extends Component {
    constructor(props) {
        super();

        this.state = {
            show: props.show,
            onClose: props.onClose
        }
    }

    renderMenuLinks = () => {
        const showHideClassName = this.state.show ? "sidebar-menu sidebar-menu--show" : "sidebar-menu sidebar-menu--fade";

        const menuLinks = data.MenuLinks.map((item, index) => (
            <li key={index} className="sidebar-menu-links__item nav-links__item">
                <NavLink exact activeClassName="selected" to={item.url}>
                    <span>
                        {item.title}
                    </span>
                </NavLink>
            </li>
        ));

        return (
            <div className={showHideClassName}>
                <div className="sidebar-menu-header">
                    <h5 className="sidebar-title">
                        {this.props.modalTitle}
                    </h5>
                    <button type="button" onClick={this.state.onClose} className="sidebar-close btn">
                        <span aria-hidden="true"></span>
                    </button>
                </div>
                <div>
                    <ul className="sidebar-menu-links sidebar-menu-links__list  nav-links nav-links__list">
                        {menuLinks}
                    </ul>
                </div>

            </div>
        );
    }

    render() {
        return (
            ReactDOM.createPortal(this.renderMenuLinks(), document.getElementById("modal"))
        );
    }
}
