// react
import React from 'react';

// third-party
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';

// application
import Topbar from './Topbar';
import Logo from './Logo';
import { LogoSvg } from '../../content/images/svg';
import NavigationPanel from './NavigationPanel';

function Header(props) {
    const { layout } = props;
    let bannerSection;

    if (layout === 'compact') {
        bannerSection = (
            <div className="ekat-header__banner">
                Banner section
            </div>
        );
    }

    return (
        <div className="ekat-header">
            {/*
             // Logo panel
             */}
            <div className="ekat-header__logo-panel">
                {/*
                 // Logo
                */}
                <Logo />
                

                {/*
                 // Banner
                */}
                {bannerSection}
            </div>
            
            {/*
             // Navigation panel
             */}
            <div className="ekat-header__nav-panel">
                <NavigationPanel />

                {/*
                 // User information
                */}
                <div className="ekat-header__user-info"></div>

                {/*
                 // Basket
                */}
                <div className="ekat-header__basket">
                </div>

                {/*
                 // Language selector
                */}
                <div className="ekat-header__language"></div>
            </div>


        </div>
    );
}

Header.propTypes = {
    /** one of ['default', 'compact'] (default: 'default') */
    layout: PropTypes.oneOf(['default', 'compact']),
};

Header.defaultProps = {
    layout: 'default',
};

export default Header;
