// react
import React, { Component } from 'react';

// third-party
import PropTypes from 'prop-types';
import { Helmet } from 'react-helmet';
import { Link, Redirect, Route, Switch } from 'react-router-dom';
import { Breakpoint, BreakpointProvider } from 'react-socks';

// application
import Footer from './footer';
import Header from './header';
import MobileHeader from './mobile/MobileHeader';
import MobileMenu from './mobile/MobileMenu';

// pages
import { Search } from './pages/search/Search';
import { Orders } from './pages/orders';
import { Options } from './pages/options';
import SitePageNotFound from './pages/site/SitePageNotFound';

// data stubs
import theme from '../data/theme';


export class Layout extends Component {
    render() {
        setDefaultBreakpoints([
            { xsmall: 0 }, // all mobile devices
            { small: 576 }, // mobile devices (not sure which one's this big)
            { medium: 768 }, // ipad, ipad pro, ipad mini, etc
            { large: 992 }, // smaller laptops
            { xlarge: 1200 } // laptops and desktops
        ]);

        return (
            <React.Fragment>
                <Helmet>
                    <title>{theme.name}</title>
                    <meta name="description" content={theme.fullName} />
                </Helmet>

                <div className="ekat__main">
                    {/*TODO: create responsive header MobileHeader*/}
                    {/* <header className="ekat__header">
                        <MobileHeader />
                    </header>*/}
                    <BreakpointProvider>
                        <Breakpoint large down>
                            <header className="ekat__header">
                                <MobileHeader />
                            </header>
                        </Breakpoint>

                        <Breakpoint large up>
                            <header className="ekat__header">
                                <Header />
                            </header>
                        </Breakpoint>
                    </BreakpointProvider>

                    {/*<header className="ekat__header">
                        <Header />
                    </header>*/}

                    <div className="ekat__body">
                        {this.props.children}
                    </div>

                    <footer className="ekat__footer">
                        <Footer/>
                    </footer>
                </div>
            </React.Fragment>
        );
    }
}
