// react
import React, { Component } from 'react';

// third-party
import { Link } from 'react-router-dom';
import { IconShopCartSvg } from '../../content/images/svg';

// application
import { Sidebar } from '../shared/Sidebar';
import { Indicator } from './Indicator';

export class IndicatorCart extends Component {
    constructor(props) {
        super(props);

        this.showSidebarIndicator = this.showSidebarIndicator.bind(this);
        this.hideSidebarIndicator = this.hideSidebarIndicator.bind(this);

        this.state = {
            sidebarBasket: false
        }
    }

    showSidebarIndicator = () => {
        this.setState({
            sidebarBasket: true
        });
    }

    hideSidebarIndicator = () => {
        this.setState({
            sidebarBasket: false
        });
    }

    renderSidebarBasket = () => {
        if (this.state.sidebarBasket) {
            return (
                <Sidebar show={this.state.sidebarBasket} onClose={this.hideSidebarIndicator}>
                    <Indicator />
                </Sidebar>
            );
        }
    }

    render() {
        return (
            <div className="indicator-cart">
                <button className="indicator__button" onClick={this.showSidebarIndicator}>
                    <span className="indicator__area">
                        <IconShopCartSvg />
                        <span className="indicator__value">10</span>
                    </span>
                </button>

                {this.renderSidebarBasket()}
            </div>
        );
    }
}
