// react
import React, { Component } from 'react';

// third-party
import classNames from 'classnames';
import { Helmet } from 'react-helmet';
import { Link, matchPath, Route, Switch, Redirect } from 'react-router-dom';

// application
import QuickSearch from './QuickSearch';
import SearchMenu from './SearchMenu';


// pages
import { SearchCriterias } from './search-criterias/index';
//import { SearchCustom } from './search-custom';
//import { SearchGeneral } from './search-general';
//import { SeachGenericArticle } from './search-generic-article';
//import { SearchManufacturer } from './search-manufacturer';
//import { SearchTyre } from './search-tyre';
//import { SearchUniversal } from './search-universal';
//import { SearchVehicles } from './search-vehicles';


export default function Search(props) {
    const { match, location } = props;


    const links = [
        { title: 'Criteria search', url: 'search-criterias' }
    ].map((link) => {
        const url = `${match.url}/${link.url}`;
        const isActive = matchPath(location.pathname, { path: url });
        const classes = classNames('account-nav__item', {
            'account-nav__item--active': isActive,
        });

        return (
            <li key={link.url} className={classes}>
                <Link to={url}>{link.title}</Link>
            </li>
        );
    });

   
    return (
        <React.Fragment>
            <Helmet>
                <title>Jedan test</title>
                <meta name="description" content="test desc" />
            </Helmet>

            <div className="ekat-search">
                <div className="ekat-search__quick-search">
                    <QuickSearch />
                </div>
                <div className="ekat-search__search-menu">
                    <SearchMenu />
                    {links}
                </div>

                <div className="ekat-search__body">
                    {/*<SearchCriterias />*/}
                    <Route exact path="/search/search-criterias" component={SearchCriterias} />
                </div>
            </div>
        </React.Fragment>
    );
}
