// react
import React, { Component } from 'react';

// third-party
import { Link, Route, matchPath } from 'react-router-dom';
import { SearchCriterias } from './search-criterias';

export default class SearchMenu extends Component {


    constructor(props) {
        super(props);

        const { match, location } = props;


        this.state = {
            match: props.match,
            changeSearchCallback: props.changeSearchCallback
        };
    }

    renderLinks = () => {}

    renderMenuLinks = () => {

        const menuLinks = [
            { id: 1, title: "Criteria search", url: '/search-criterias' },
            { id: 2, title: "Custom search", url: '/search-custom' },
            { id: 3, title: "General search", url: '/search-general' },
            { id: 4, title: "Search by part name", url: '/search-generic-article' },
            { id: 5, title: "Search by manufacturer", url: '/search-manufacturer' },
            { id: 6, title: "Tyre search", url: '/search-tyre' },
            { id: 7, title: "Universal search", url: '/search-universal' },
            { id: 8, title: "Vehicle search", url: '/search-vehicles' }
        ];

        //return (
        //    menuLinks.map((item) => {
        //        const url = `${this.state.match.url}/${item.url}`;

        //        return (
        //            <li key={item.url}>
        //                <Link to={url}>{item.title}</Link>
        //            </li>
        //        );
        //    }));

        return (
            menuLinks.map((item, index) => (
                <li key={item.url} className="nav-links__item">
                    <button onClick={() => { this.state.changeSearchCallback(item.id) }}>
                        <span>
                            {item.title}
                        </span>
                    </button>
                </li>
            )));
    }

    render() {
        return (
            <div className="search-menu">
                <ul className="search-menu__list">
                    {this.renderMenuLinks()}
                </ul>
                <div>
                    <Route exact path={`${this.state.match.path}/search-criterias`} component={SearchCriterias} />
                </div>
            </div>
        );
    }
}
