// react
import React, { Component } from 'react';

// third-party
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useParams
} from "react-router-dom";
import { IconClose20Svg } from '../../../content/images/svg';


export default class SearchMenu extends Component {

    constructor(props) {
        super();

        this.state = {
            changeSearchCallback: props.changeSearchCallback,
            selectedItem: props.menuItem
        };
    }



    renderMenuLinks = () => {
        const menuLinks = [
            { id: 1, icon: <IconClose20Svg />, title: "Criteria search", url: '/search/search-criterias' },
            { id: 2, icon: <IconClose20Svg />, title: "Custom search", url: '/search/search-custom' },
            { id: 3, icon: <IconClose20Svg />, title: "General search", url: '/search/search-general' },
            { id: 4, icon: <IconClose20Svg />, title: "Search by part name", url: '/search/search-generic-article' },
            { id: 5, icon: <IconClose20Svg />, title: "Search by manufacturer", url: '/search/search-manufacturer' },
            { id: 6, icon: <IconClose20Svg />, title: "Tyre search", url: '/search/search-tyre' },
            { id: 7, icon: <IconClose20Svg />, title: "Universal search", url: '/search/search-universal' },
            { id: 8, icon: <IconClose20Svg />, title: "Vehicle search", url: '/search/search-vehicles' }
        ];
        
        return (
            menuLinks.map((item, index, menuItem) => (
                <Router>
                    <div>
                        <li>
                            <Link to="/netflix">Netflix</Link>
                        </li>
                        <li>
                            <Link to="/zillow-group">Zillow Group</Link>
                        </li>
                        <li>
                            <Link to="/yahoo">Yahoo</Link>
                        </li>


                        <li key={item.url} onClick={() => { this.state.changeSearchCallback(item.id) }} className={this.state.selectedItem === menuItem ? "search-menu__item selected" : "search-menu__item"}>
                            <Link to={item.url}>
                                <span>
                                    {item.icon}
                                    <span>
                                        {item.title}
                                    </span>
                                </span>
                            </Link>
                        </li>
                        <Switch>
                            <Route path="/:url" children={<Child />} />
                        </Switch>
                    </div>
                </Router>
            ))
        );
    }

    render() {
        return (
            <div className="search-menu">
                <ul className="search-menu__list">
                    {this.renderMenuLinks()}
                </ul>
            </div>
        );
    }
}

function Child() {
    // We can use the `useParams` hook here to access
    // the dynamic pieces of the URL.
    let { url } = useParams();

    return (
        <div>
            <h3>ID: {url}</h3>
        </div>
    );
}
