// react
import React from 'react';

// third-party
import classNames from 'classnames';
import { Helmet } from 'react-helmet';
import {
    Link,
    matchPath,
    NavLink,
    Redirect,
    Switch,
    Route,
} from 'react-router-dom';

// application
import QuickSearch from './QuickSearch';
import SearchMenu from './SearchMenu';


// pages
import SearchCriterias from './search-criterias';
//import { SearchCustom } from './search-custom';
//import { SearchGeneral } from './search-general';
//import { SeachGenericArticle } from './search-generic-article';
//import { SearchManufacturer } from './search-manufacturer';
//import { SearchTyre } from './search-tyre';
//import { SearchUniversal } from './search-universal';
//import { SearchVehicles } from './search-vehicles';

export default function Search(props) {
    const { match, location } = props;

    const breadcrumb = [
        { title: 'Home', url: '' },
        { title: 'My Account', url: '' },
    ];

    const links = [
        { title: 'Dashboard', url: 'dashboard' },
        { title: 'Edit Profile', url: 'profile' },
        { title: 'Order History', url: 'orders' },
        { title: 'Addresses', url: 'addresses' },
        { title: 'Password', url: 'password' },
        { title: 'Logout', url: 'login' },
    ].map((link) => {
        const url = `${match.url}/${link.url}`;
        const isActive = matchPath(location.pathname, { path: url });
        const classes = classNames('account-nav__item', {
            'account-nav__item--active': isActive,
        });

        return (
            <li key={link.url} className={classes}>
                <Link to={url}>{link.title}</Link>
            </li>
        );
    });


    const menuLinks = [
        { title: "Criteria search", url: '/search/search-criterias' },
        { title: "Custom search", url: '/search-custom' },
        { title: "General search", url: '/search-general' },
        { title: "Search by part name", url: '/search-generic-article' },
        { title: "Search by manufacturer", url: '/search-manufacturer' },
        { title: "Tyre search", url: '/search-tyre' },
        { title: "Universal search", url: '/search-universal' },
        { title: "Vehicle search", url: '/search-vehicles' }
    ];

    const menuLinksList = menuLinks.map((item, index) => (
        <li key={index} className="nav-links__item">
            <NavLink exact activeClassName="selected" to={item.url}>
                <span>
                    {item.title}
                </span>
            </NavLink>
        </li>
    ));


    return (
        <React.Fragment>
            <Helmet>
                <title>Jedan test</title>
                <meta name="description" content="test desc" />
            </Helmet>

            <div className="ekat-search">
                <div className="ekat-search__quick-search">
                    <QuickSearch />
                </div>
                <div className="ekat-search__search-menu">
                    <div className="search-menu">
                        <ul className="search-menu__list">
                            {links}
                            {menuLinksList}
                        </ul>
                    </div>
                    {/*<SearchMenu />*/}
                </div>

                <div className="ekat-search__body">
                    <Switch>

                        <Route exact path={`${match.path}/search-criterias`} component={SearchCriterias} />

                        <Route exact path="/search/search-criterias" component={SearchCriterias} />
                    </Switch>
                </div>
            </div>
        </React.Fragment>
    );
    
}
