// react
import React, { Component } from 'react';

// third-party
import {
    BrowserRouter,
    Route,
    Redirect,
    Switch,
} from 'react-router-dom';

// pages
import { Layout } from './Layout';
import { SearchLayout } from './pages/search/SearchLayout';
import Search  from './pages/search/index';
import { Orders } from './pages/orders/index';
import { Options } from './pages/options/index';
import SitePageNotFound from './pages/site/SitePageNotFound';

import SearchCriterias from './pages/search/search-criterias/index';

export default class Root extends Component {
    componentDidMount() {
        setTimeout(() => {
            const preloader = document.querySelector('.site-preloader');

            preloader.addEventListener('transitionend', (event) => {
                if (event.propertyName === 'opacity') {
                    preloader.parentNode.removeChild(preloader);
                }
            });
            preloader.classList.add('site-preloader__fade');
        }, 500);
    }


    routes = () => {
        const routes = [
            {
                path: "/sandwiches",
                component: Search
            },
            {
                path: "/tacos",
                component: Orders,
                routes: [
                    {
                        path: "/tacos/bus",
                        component: Bus
                    },
                    {
                        path: "/tacos/cart",
                        component: Cart
                    }
                ]
            }
        ];

        return (
            routes.map((route, i) => (
                <Layout key={i} {...route} />
            ))
        );
    }

    render() {
        return (
            <BrowserRouter>

                    <Switch>
                        {this.routes()}

                        <Route exact path='/' render={() => <Redirect to="/search" />} />

                        <Route exact path="/search" component={Search} />
                        <Route exact path="/orders" component={Orders} />
                        <Route exact path="/options" component={Options} />
                        
                        {/*<Route exact path="/site/not-found" component={SitePageNotFound} />
                        <Route component={SitePageNotFound} />*/}
                    </Switch>


                {/*<SearchLayout>
                    <Switch>

                        <Route exact path="/search-criterias" component={SearchCriterias} />

                    </Switch>
                </SearchLayout>*/}
            </BrowserRouter>
        );
    }
}
