// react
import React, { Component } from 'react';

// third-party
import ReactDOM from 'react-dom';
import PropTypes from 'prop-types';

export class Modal extends Component {
    constructor(props) {
        super();

        this.state = {
            name: props.name,
            show: props.show,
            modalTitle: props.modalTitle,
            children: props.children,
            onClose: props.onClose
        }
    }

    componentDidMount() {
        document.addEventListener('mousedown', this.handleOutsideClick);
    }

    componentWillUnmount() {
        document.removeEventListener('mousedown', this.handleOutsideClick);
    }

    setWrapperRef = (node) => {
        this.wrapperRef = node;
    };

    handleOutsideClick = (event) => {
        const { open } = this.state;

        if (this.wrapperRef && this.wrapperRef.contains(event.target) && open) {
            this.close();
        }

        //if (this.node.contains(event.target)) {
        //    this.close();
        //}
    };

    open() {
        this.setState(() => ({
            open: true,
        }));
    }

    close() {
        this.setState(() => ({
            open: false,
        }));
    }

    renderModal = () => {
        const showHideClassName = this.state.show ? "modal modal-show display-block" : "modal modal-hide display-none";

        return (
            <div className={showHideClassName} ref={node => this.node = node}>
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h1>

                            </h1>
                            <button onClick={this.state.onClose}>
                                close
                            </button>
                        </div>
                        <div className="modal-body">
                            {this.props.children}
                        </div>
                        <div className="modal-footer">
                            <div>

                            </div>
                            <div>

                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }

    renderSmallModal = () => {
        const showHideClassName = this.state.show ? "modal modal-sm display-block" : "modal modal-sm display-none";

        return (
            <div className={showHideClassName}>
                <section className="modal-main">

                    <button onClick={this.state.onClose}>
                        close
                    </button>
                </section>
            </div>
        );
    }

    render() {
        switch (this.state.name) {
            case "modal":
                return (
                    ReactDOM.createPortal(this.renderModal(), document.getElementById("modal"))
                );
            case "modal-sm":
                return (
                    ReactDOM.createPortal(this.renderSmallModal(), document.body)
                    //<React.Fragment>
                    //    {this.renderSmallModal()}
                    //</React.Fragment>
                );
            default:
        }
    }
}
