// react
import React, { Component } from 'react';

// third-party
import { NavLink } from 'react-router-dom';
import { IconMenuDotsSvg } from '../../content/images/svg';
import { Menu } from './Menu';

// pages
import { Sidebar } from './../shared/Sidebar';

// data
import data from './../../data/data';

export class MenuDots extends Component {
    constructor(props) {
        super(props);
        
        this.showSidebarMenu = this.showSidebarMenu.bind(this);
        this.hideSidebarMenu = this.hideSidebarMenu.bind(this);
       
        this.state = {
            sidebarInfo: false
        }
    }

    showSidebarMenu = () => {
        this.setState({
            sidebarInfo: true
        });
    }
    hideSidebarMenu = () => {
        this.setState({
            sidebarInfo: false
        });
    }

    renderSidebarMenu() {
        const menuLinks = data.MenuLinks.map((item, index) => (
            <li key={index} className="sidebar-menu-links__item nav-links__item">
                <NavLink exact activeClassName="selected" to={item.url}>
                    <span>
                        {item.title}
                    </span>
                </NavLink>
            </li>
        ));



        if (this.state.sidebarInfo) {
            return (
                <Sidebar show={this.state.sidebarInfo} onClose={this.hideSidebarMenu}>
                    <ul className="sidebar-menu-links sidebar-menu-links__list  nav-links nav-links__list">
                        {menuLinks}
                    </ul>
                </Sidebar>
            );
        }
    }

    render() {
        return (
            <div className="menu-dots">
                <button className="menu-dots__button" onClick={this.showSidebarMenu} title="Menu">
                    <span>
                        <IconMenuDotsSvg className="menu-dots__button-icon" />
                    </span>
                </button>

                {this.renderSidebarMenu()}
                <Menu />
            </div>
        );
    }
}
