// react
import React, { Component } from 'react';

// third-party
import { NavLink } from 'react-router-dom';

// data
import data from './../../data/data';

export class SidebarMenu extends Component {
    constructor(props) {
        super();

        this.state = {
            show: props.show
        }
    }

    render() {
        const showHideClassName = this.state.show ? "sidebar-menu sidebar-menu--show" : "sidebar-menu sidebar-menu--fade";

        const menuLinks = data.MenuLinks.map((item, index) => (
            <li key={index} className="sidebar-menu-links__item nav-links__item">
                <NavLink exact activeClassName="selected" to={item.url}>
                    <span>
                        {item.title}
                    </span>
                </NavLink>
            </li>
        ));

        return (
            <div className={showHideClassName}>
                <ul className="sidebar-menu-links sidebar-menu-links__list  nav-links nav-links__list">
                    {menuLinks}
                </ul>
            </div>
        );
    }
}
