// react
import React, { Component } from 'react';

// third-party
import { Helmet } from 'react-helmet';
import { Route, Switch, Redirect } from 'react-router-dom';

// application
import QuickSearch from './QuickSearch';
import SearchMenu from './SearchMenu';

// pages
import { IndexSearch } from './IndexSearch';
import { SearchCriterias } from './search-criterias/SearchCriterias';
import { SearchCustom } from './search-custom/SearchCustom';
import { SearchGeneral } from './search-general/SearchGeneral';
import { SearchGenericArticle } from './search-generic-article/SearchGenericArticle';
import { SearchManufacturer } from './search-manufacturer/SearchManufacturer';
import { SearchTyre } from './search-tyre/SearchTyre';
import { SearchUniversal } from './search-universal/SearchUniversal';
import { SearchVehicles } from './search-vehicles/SearchVehicles';


export class Search extends Component {

    constructor() {
        super();
        this.state = {
            currentSearch: 0
        };
    }

    renderCurrentSearch = () => {
        switch (this.state.currentSearch) {
            case 0:
                return <IndexSearch />;
            case 1:
                return <SearchCriterias />;
            case 2:
                return <SearchCustom />;
            case 3:
                return <SearchGeneral />;
            case 4:
                return <SearchGenericArticle />;
            case 5:
                return <SearchManufacturer />;
            case 6:
                return <SearchTyre />;
            case 7:
                return <SearchUniversal />;
            case 8:
                return <SearchVehicles />;
        }
    }

    changeSearchCallback = (id) => {
        this.setState({ currentSearch: id });
    }

    render() {
        return (
            <React.Fragment>
                <Helmet>
                    <title>Jedan test</title>
                    <meta name="description" content="test desc" />
                </Helmet>

                <div className="ekat-search">
                    <div className="ekat-search__quick-search">
                        <QuickSearch />
                    </div>
                    <div className="ekat-search__main">
                        <div className="ekat-search__menu">
                            <SearchMenu changeSearchCallback={this.changeSearchCallback} />
                        </div>
                        <div className="ekat-search__body">
                            {/*
                            // TODO: Create partial JSX for different search
                            */}

                            {this.renderCurrentSearch()}
                        </div>
                    </div>
                </div>
            </React.Fragment>
        )
    };
}
