// react
import React, { Component } from 'react';

// third-party
import {
    BrowserRouter,
    Route,
    Redirect,
    Switch,
} from 'react-router-dom';

// pages
import { Layout } from './Layout';
//import { SearchLayout } from './pages/search/SearchLayout';
import { Search } from './pages/search/Search';
import { IndexSearch } from './pages/search/IndexSearch';
import { SearchCriterias } from './pages/search/search-criterias/SearchCriterias';
import { SearchCustom } from './pages/search/search-custom/SearchCustom';
import { SearchGeneral } from './pages/search/search-general/SearchGeneral';
import { SearchGenericArticle } from './pages/search/search-generic-article/SearchGenericArticle';
import { SearchManufacturer } from './pages/search/search-manufacturer/SearchManufacturer';
import { SearchTyre } from './pages/search/search-tyre/SearchTyre';
import { SearchUniversal } from './pages/search/search-universal/SearchUniversal';
import { SearchVehicles } from './pages/search/search-vehicles/SearchVehicles';



import { Orders } from './pages/orders/Orders';
import { Options } from './pages/options/Options';

import { Article } from './pages/article/Article';

import SitePageNotFound from './pages/site/SitePageNotFound';
//import { SearchCriterias } from './pages/search/search-criterias/SearchCriterias';

export default class Root extends Component {
    componentDidMount() {
        setTimeout(() => {
            const preloader = document.querySelector('.site-preloader');

            preloader.addEventListener('transitionend', (event) => {
                if (event.propertyName === 'opacity') {
                    preloader.parentNode.removeChild(preloader);
                }
            });
            preloader.classList.add('site-preloader__fade');
        }, 500);
    }

    render() {
        return (
            <BrowserRouter>
                <Layout>
                    <Switch>
                        <Route exact path='/' render={() => <Redirect to="/search" />} />

                        <Route exact path="/search" component={Search} />

                        <Route exact path='/search/:id' component={() => (<IndexSearch />)} />
                        <Route exact path='/search/:id' component={() => (<SearchCriterias />)} />
                        <Route exact path='/search/:id' component={() => (<SearchCustom />)} />
                        <Route exact path='/search/:id' component={() => (<SearchGeneral />)} />
                        <Route exact path='/search/:id' component={() => (<SearchGenericArticle />)} />
                        <Route exact path='/search/:id' component={() => (<SearchManufacturer />)} />
                        <Route exact path='/search/:id' component={() => (<SearchTyre />)} />
                        <Route exact path='/search/:id' component={() => (<SearchUniversal />)} />
                        <Route exact path='/search/:id' component={() => (<SearchVehicles />)} />



                        <Route exact path="/orders" component={Orders} />
                        <Route exact path="/options" component={Options} />

                        <Route exact path="/article/:id" component={() => (<Article />)} />

                        {/*<Route exact path="/search-criterias" component={() => (<div>Search test<SearchCriterias/></div>)} />*/}
                        {/*<Route exact path="/search/search-criterias" component={SearchCriterias} />*/}

                        

                        <Route exact path="/site/not-found" component={SitePageNotFound} />
                        <Route component={SitePageNotFound} />
                    </Switch>
                </Layout>

                {/*<SearchLayout>
                    <Switch>

                    </Switch>
                </SearchLayout>*/}
            </BrowserRouter>
        );
    }
}
