// react
import React, { Component } from 'react';

// third-party
import {
    BrowserRouter,
    Route,
    Redirect,
    Switch,
} from 'react-router-dom';
import Routes from './Routes';

// pages
import { Layout } from './Layout';
import { SearchLayout } from './pages/search/SearchLayout';

import { Search } from './pages/search/Search';
import { Orders } from './pages/orders/Orders';
import { Options } from './pages/options/Options';

import { Article } from './pages/article/Article';

import SitePageNotFound from './pages/site/SitePageNotFound';

export default class Root extends Component {
    componentDidMount() {
        setTimeout(() => {
            const preloader = document.querySelector('.site-preloader');

            preloader.addEventListener('transitionend', (event) => {
                if (event.propertyName === 'opacity') {
                    preloader.parentNode.removeChild(preloader);
                }
            });
            preloader.classList.add('site-preloader__fade');
        }, 500);
    }

    render() {
        return (
            <BrowserRouter>
                <Switch>
                    <Route exact path='/' render={() => <Redirect to="/search" />} />

                    <Routes exact path="/search" component={Search} layout={Layout} />

                    <Routes exact path='/search/:url' component={Search} layout={Layout} />

                    <Route exact path="/orders" component={Orders} layout={SearchLayout} />
                    <Route exact path="/options" component={Options} layout={SearchLayout} />


                    <Route exact path="/site/not-found" component={SitePageNotFound} />
                    <Route component={SitePageNotFound} />

                </Switch>

                <Layout>
                    <Switch>

                      


                        
                    </Switch>
                </Layout>

            </BrowserRouter>
        );
    }
}
