// react
import React, { Component } from 'react';

// third-party
import PropTypes from 'prop-types';
import { Helmet } from 'react-helmet';
import { Link, Redirect, Route, Switch } from 'react-router-dom';
import { Breakpoint, BreakpointProvider } from 'react-socks';

// application
import Footer from './footer';
import Header from './header';
import MobileHeader from './mobile/MobileHeader';
import MobileMenu from './mobile/MobileMenu';

// pages
import { Search } from './pages/search/index';
import { Orders } from './pages/orders/index';
import { Options } from './pages/options/index';
import SitePageNotFound from './pages/site/SitePageNotFound';

import { SearchCriterias } from './pages/search/search-criterias/index';
import { SearchCustom } from './pages/search/search-custom/index';
import { SearchGeneral } from './pages/search/search-general/index';
import { SeachGenericArticle } from './pages/search/search-generic-article/index';
import { SearchManufacturer } from './pages/search/search-manufacturer/index';
import { SearchTyre } from './pages/search/search-tyre/index';
import { SearchUniversal } from './pages/search/search-universal/index';
import { SearchVehicles } from './pages/search/search-vehicles/index';

// data stubs
import theme from '../data/theme';


export class Layout extends Component {
    render() {
        return (
            <React.Fragment>
                <Helmet>
                    <title>{theme.name}</title>
                    <meta name="description" content={theme.fullName} />
                </Helmet>

                <div className="ekat__main">
                    {/*TODO: create responsive header MobileHeader*/}
                    {/* <header className="ekat__header">
                        <MobileHeader />
                    </header>*/}
                    {/*<BreakpointProvider>
                        <Breakpoint small down>
                            <header className="ekat__header">
                                <MobileHeader />
                            </header>
                        </Breakpoint>

                        <Breakpoint medium up>
                            <header className="ekat__header">
                                <Header />
                            </header>
                        </Breakpoint>
                    </BreakpointProvider>*/}

                    <header className="ekat__header">
                        <Header />
                    </header>

                    <div className="ekat__body">
                        {this.props.children}
                        <Switch>
                            {/*
                            // Home page to general search
                            */}
                            <Route
                                exact
                                path='/'
                                render={() => <Redirect to="/search" />} />


                            {/*
                            // Search
                            */}
                            <Route exact path="/search" component={Search} />

                            {/*
                            // Orders
                            */}
                            <Route exact path="/orders" component={Orders} />

                            {/*
                            // Options
                            */}
                            <Route exact path="/options" component={Options} />


                            {/*
                             // Site
                             */}
                            <Route exact path="/site/not-found" component={SitePageNotFound} />

                            {/*
                            // Page Not Found - page for errors
                            */}
                            <Route component={SitePageNotFound} />


                            <Route exact path="/search-criterias" component={SearchCriterias} />
                            <Route exact path="/search-custom" component={SearchCustom} />
                        </Switch>
                    </div>

                    <footer className="ekat__footer">
                        <Footer/>
                    </footer>
                </div>
            </React.Fragment>
        );
    }
}
