// react
import React, { Component } from 'react';

// third-party
import { Link, NavLink } from 'react-router-dom';
import { IconMenuSvg, IconArrowDown20Svg, IconClose20Svg, IconReset20Svg } from '../../../content/images/svg';


export default class SearchMenu extends Component {

    constructor(props) {
        super();

        this.state = {
            changeSearchCallback: props.changeSearchCallback
        };
    }

    renderMenuLinks = () => {
        const menuLinks = [
            { id: 1, icon: <IconClose20Svg />, title: "Criteria search", url: '/search/search-criterias' },
            { id: 2, title: "Custom search", url: '/search/search-custom' },
            { id: 3, title: "General search", url: '/search/search-general' },
            { id: 4, title: "Search by part name", url: '/search/search-generic-article' },
            { id: 5, title: "Search by manufacturer", url: '/search/search-manufacturer' },
            { id: 6, title: "Tyre search", url: '/search/search-tyre' },
            { id: 7, title: "Universal search", url: '/search/search-universal' },
            { id: 8, title: "Vehicle search", url: '/search/search-vehicles' }
        ];

        return (
            menuLinks.map((item, index) => (
                <li key={item.url} className="nav-links__item">
                    <Link onClick={() => { this.state.changeSearchCallback(item.id) }}>
                        {item.icon}
                        <span>

                        </span>
                    </Link>
                </li>
            )));
    }

    render() {
        return (
            <div className="search-menu">
                <ul className="search-menu__list">
                    {this.renderMenuLinks()}
                </ul>
            </div>
        );
    }
}
