// react
import React, { Component } from 'react';

// third-party
import { NavLink } from 'react-router-dom';
import { IconAlertSvg, IconInformationSvg, IconLegendSvg } from '../../content/images/svg';

// pages
import { Modal } from './../shared/Modal';

export class NavIcons extends Component {
    constructor(props) {
        super();

        this.showModal = this.showModal.bind(this);
        this.hideModal = this.hideModal.bind(this);

        this.state = {
            //handleClose: props.handleClose,
            modalNewBodyshop: false
        }
    }

    showModal = () => {
        this.setState({
            modalNewBodyshop: true
        });
    };

    hideModal = () => {
        this.setState({
            modalNewBodyshop: false
        });
    };

    renderModals() {
        if (this.state.modalNewBodyshop) {
            return <Modal name="modal" show={this.state.modalNewBodyshop} onClose={this.state.hideModal} />;
        }
    }

    render() {
        return (
            <React.Fragment>
                <div className="nav-icons">
                    <div>
                        <button type="button" onClick={this.showModal}>
                            <span>
                                <IconInformationSvg />
                            </span>
                        </button>
                        <NavLink exact activeClassName="selected" to="/">
                            <span>
                                <IconInformationSvg />
                            </span>
                        </NavLink>
                    </div>
                    <div>
                        <NavLink exact activeClassName="selected" to="/">
                            <span>
                                <IconAlertSvg />
                            </span>
                        </NavLink>
                    </div>
                    <div>
                        <NavLink exact activeClassName="selected" to="/">
                            <span>
                                <IconLegendSvg className="nav-icons__button-icon" />
                            </span>
                        </NavLink>
                    </div>
                </div>

                {this.renderModals()}
            </React.Fragment>
        );
    }
}
