// react
import React, { Component } from 'react';

// third-party
import { Link } from 'react-router-dom';

// application
import Indicator from './Indicator';
import { IconShopCartSvg, IconCross10Svg } from '../../content/images/svg';

export class IndicatorCart extends Component {

    renderDropCartProduct = () => {
        return (
            <div class="dropcart__product">
                <div class="dropcart__product-image">
                    <Link to="">

                    </Link>
                </div>
                <div class="dropcart__product-info">
                    <div class="dropcart__product-name">
                        <a href="/shop/product/2">Undefined Tool IRadix DPS3000SY 2700 Watts</a>
                    </div>
                    <ul class="dropcart__product-options"></ul>
                    <div class="dropcart__product-meta">
                        <span class="dropcart__product-quantity">1</span> x <span class="dropcart__product-price">$1019.00</span>
                    </div>
                </div>

                <button type="button" className="dropcart__product-remove btn btn-light btn-sm btn-svg-icon">
                    <IconCross10Svg />
                </button>
            </div>
        );
    }

    renderDropCartTotals = () => {
        return (
            <div class="dropcart__totals">
                <table>
                    <tbody>
                        <tr>
                            <th>Subtotal</th>
                            <td>$1968.00</td>
                        </tr>
                        <tr>
                            <th>Shipping</th>
                            <td>$25.00</td>
                        </tr>
                        <tr>
                            <th>Tax</th>
                            <td>$0.00</td>
                        </tr>
                        <tr>
                            <th>Total</th>
                            <td>$1993.00</td>
                        </tr>
                    </tbody>
                </table>
            </div>
        );
    }

    renderDropCartButtons = () => {
        return (
            <div className="dropcart__buttons">
                <Link className="btn btn-secondary" to="/shop/cart">View Cart</Link>
                <Link className="btn btn-primary" to="/shop/checkout">Checkout</Link>
            </div>
        );
    }

    render() {
        let dropdown;

        if (true) {
            dropdown = (
                <div className="dropcart">
                    <div className="dropcart__products-list">
                        {this.renderDropCartProduct()}

                        {this.renderDropCartTotals()}

                        {this.renderDropCartButtons()}
                    </div>
                </div>
            );
        } else {
            dropdown = (
                <div className="dropcart">
                    <div className="dropcart__empty">
                        Your shopping cart is empty!
                    </div>
                </div>
            );
        }

        return (
            <Indicator url="/" dropdown={dropdown} icon={<IconShopCartSvg />} />
        );
    }
}
