// react
import React, { Component } from 'react';

// third-party
import { Link } from 'react-router-dom';

// application
import Indicator from './Indicator';
import { IconShopCartSvg, IconCross10Svg } from '../../content/images/svg';

export class IndicatorCart extends Component {

    renderDropCartProduct = () => {
        const products = [
            {
                id: 1, image: "http://images.local.automarket.rs/Common/images/no_image170.jpg", title: "Agip", manufacturer: "Agip", articleNumber: "123", description: "Zadnja vrata(Karoserija)", price: "10.00", quantity: "9,999", currency: "RSD"
            }
        ];

        const productItem = products.map((item, index) => {
            let description;
            let image;

            if (item.description) {
                description = (
                    <ul key={index} className="dropcart__product-options">
                        <li>
                            {item.description}
                        </li>
                    </ul>
                );
            }

            if (item.image.length) {
                image = (
                    <div className="dropcart__product-image">
                        <Link to={`/product/${item.id}`}>
                            <img src={item.image} alt={item.manufacturer + ', ' + item.articleNumber} />
                        </Link>
                    </div>
                );
            }

            const removeButton = (
                <button type="button" className="dropcart__product-remove btn btn-light btn-sm btn-svg-icon">
                    <IconCross10Svg />
                </button>
            );

            return (
                <div key={index} className="dropcart__product">
                    {image}

                    <div className="dropcart__product-info">
                        <div className="dropcart__product-name">
                            <Link to={`/${item.id}`}>
                                {item.manufacturer} - {item.articleNumber}
                            </Link>
                        </div>
                        {description}
                        <div className="dropcart__product-meta">
                            <span className="dropcart__product-quantity">{item.quantity}</span>
                            {' x '}
                            <span className="dropcart__product-price">{item.price} {item.currency}</span>
                        </div>
                    </div>

                    {removeButton}
                </div>
            );
        });

        return (
            <React.Fragment>
                {productItem}
            </React.Fragment>
        );
    }

    renderDropCartTotals = () => {
        return (
            <div className="dropcart__totals">
                <table>
                    <tbody>
                        <tr>
                            <th>Subtotal</th>
                            <td>$1968.00</td>
                        </tr>
                        <tr>
                            <th>Shipping</th>
                            <td>$25.00</td>
                        </tr>
                        <tr>
                            <th>Tax</th>
                            <td>$0.00</td>
                        </tr>
                        <tr>
                            <th>Total</th>
                            <td>$1993.00</td>
                        </tr>
                    </tbody>
                </table>
            </div>
        );
    }

    renderDropCartButtons = () => {
        return (
            <div className="dropcart__buttons">
                <Link className="btn btn-secondary" to="/shop/cart">View Cart</Link>
                <Link className="btn btn-primary" to="/shop/checkout">Checkout</Link>
            </div>
        );
    }

    render() {
        let dropdown;

        if (true) {
            dropdown = (
                <div className="dropcart">
                    <div className="dropcart__products-list">
                        {this.renderDropCartProduct()}

                        {this.renderDropCartTotals()}

                        {this.renderDropCartButtons()}
                    </div>
                </div>
            );
        } else {
            dropdown = (
                <div className="dropcart">
                    <div className="dropcart__empty">
                        Your shopping cart is empty!
                    </div>
                </div>
            );
        }

        return (
            <Indicator url="/" dropdown={dropdown} icon={<IconShopCartSvg />} />
        );
    }
}
